import { useRef } from "react"
import "./modify.css"
import axios from "axios"
import { AuthContext } from "../../context/AuthContext"
import { useContext } from "react"

export default function Modify() {
  const {user} = useContext(AuthContext)

  const submitHandler = async (e) => {
    e.preventDefault()
    const userId = e.target.userId.value;
    const nodes = e.target.nodes.value;

    const newPost = {
      desc: nodes
    }

      await axios.put("http://localhost:8800/api/posts/modify/" + userId, newPost)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
         console.log(error);
      })
  }

  return (
    <div className="share">
      <form className="shareBottom02" onSubmit={submitHandler}>
        <input
          type="text"
          placeholder="cluster name" 
          className="shareInput"
          id="userId"
          required
          name="userId"
        />
        <input
          type="text"
          placeholder="amount of nodes" 
          className="shareInput"
          id="nodes"
          required
          name="nodes"
        />
        <button className="shareButton02" type="submit">Amount of nodes</button>  
      </form>  
    </div>
  )
}
